package com.resliv.bot.TelegramBot.appconfig;import com.resliv.bot.TelegramBot.handler.TelegramBot;import com.resliv.bot.TelegramBot.service.CityService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.telegram.telegrambots.bots.TelegramLongPollingBot;import org.telegram.telegrambots.meta.TelegramBotsApi;import org.telegram.telegrambots.meta.exceptions.TelegramApiException;import org.telegram.telegrambots.updatesreceivers.DefaultBotSession;@Configurationpublic class Config {    @Autowired    private TelegramLongPollingBot telegramBotApiListener;    @Bean    public TelegramLongPollingBot telegramBotApiListener(CityService cityService) {        return new TelegramBot(cityService);    }    @Bean    public void botConnect() {        try {            TelegramBotsApi telegramBotsApi = new TelegramBotsApi(DefaultBotSession.class);            telegramBotsApi.registerBot(telegramBotApiListener);        } catch (TelegramApiException e) {            e.printStackTrace();        }    }}